def factorial(n):
    result = 1
    for i in range(1, n + 1):
        result *= i
    return result

def fibonacci(n):
    if n <= 1:
        return n
    return fibonacci(n - 1) + fibonacci(n - 2)

def sum_list(numbers):
    total = 0
    for num in numbers:
        total += num
    return total

def reverse_string(s):
    result = ''
    for char in s:
        result = char + result
    return result

def is_palindrome(s):
    s = s.lower()
    for i in range(len(s) // 2):
        if s[i] != s[-(i + 1)]:
            return False
    return True

def max_in_list(numbers):
    max_val = numbers[0]
    for num in numbers:
        if num > max_val:
            max_val = num
    return max_val

def count_vowels(s):
    vowels = 'aeiou'
    count = 0
    for char in s.lower():
        if char in vowels:
            count += 1
    return count

def power(base, exp):
    result = 1
    for _ in range(exp):
        result *= base
    return result

def unique_elements(lst):
    result = []
    for item in lst:
        if item not in result:
            result.append(item)
    return result

def sort_list(lst):
    n = len(lst)
    for i in range(n):
        for j in range(0, n - i - 1):
            if lst[j] > lst[j + 1]:
                lst[j], lst[j + 1] = lst[j + 1], lst[j]
    return lst
